module.exports = function (RED) {
    "use strict";

    function AndNode(n) {
        RED.nodes.createNode(this, n);
        const node = this;
        this.name = n.name;
        this.property = n.property;
        this.propertyType = n.propertyType || "msg";
        this.count = parseInt(n.count);
        this.micropythonCode = n.micropythonCode || "";
        
        this.predicates = n.predicates.length === 0 ? [] : n.predicates.split(" "); 
        this.priorities = n.priorities.length === 0 ? [] : n.priorities.split(" "); 
        this.setPredicates(this.predicates);
        this.setPriorities(this.priorities);

        this.setGenerateCodeFunction(() => generateMicropythonCode(this.property, this.count));

        this.topics = []
        this.inputs = [];

        this.on("input", function (msg, done) {
            try {
                const value = RED.util.getMessageProperty(msg,this.property);

                if (msg.hasOwnProperty("topic") && this.topics.indexOf(msg.topic) === -1) {
                    this.inputs.push(value);
                    this.topics.push(msg.topic);
                }

                if (this.topics.length === this.count) {
                    let result = true;
                    for (let i = 0; i < this.inputs.length; i++) {
                        result = result && this.inputs[i];
                    }

                    const msg = { payload: result };
                    node.send(msg);

                    this.topics = [];
                    this.inputs = [];
                }
            } catch (err) {
                done(JSON.stringify(err));
            }
        });
        this.on("close", function () {
            this.status({});
        });

        generateMicropythonCode(this.property, this.count);

        /**
         * [MINE]
         * 
         * Generates micropython code that exxecutes the switch behaviour
         * 
         * @param {*} rules 
         */
        function generateMicropythonCode(property, count) {
            const code =
                `\ninput_topics_${node.textId} = [${node.inputTopics.map(a => `"${a}"`)}]
output_topics_${node.textId} = [${node.outputTopics.map(a => `"${a}"`)}]
nr_inputs_${node.textId} = ${count}
property_${node.textId} = "${property}"
inputs_${node.textId} = []
topics_${node.textId} = []

def get_property_value_${node.textId}(msg):
    properties = property_${node.textId}.split(".")
    payload = ujson.loads(msg)

    for property in properties:
        try:
            if payload[property]:
                payload = payload[property]
            else:
                print("${node.textId}: Property not found")
                break
        except:
            print("${node.textId}: Msg is not an object")
            break

    return payload

def on_input_${node.textId}(topic, msg, retained):
    global inputs_${node.textId}
    global topics_${node.textId}

    if not topic in topics_${node.textId}:
        topics_${node.textId}.append(topic)
        msg = get_property_value_${node.textId}(msg)
        if (msg == 'True') or (msg == 'true'):
            inputs_${node.textId}.append(True)
        elif (msg == 'False') or (msg == 'false'):
            inputs_${node.textId}.append(False)
    
    if len(topics_${node.textId}) == nr_inputs_${node.textId}:
        result = True
        for entry in inputs_${node.textId}:
            result = result and entry
        res = dict(
            payload=str(result)
        )
        loop = asyncio.get_event_loop()
        loop.create_task(on_output(ujson.dumps(res), output_topics_${node.textId}))
        inputs_${node.textId} = []
        topics_${node.textId} = []
    
    return\n`

            node.setMicropythonCode(code);
        }

    }
    RED.nodes.registerType("and", AndNode);
};
